import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { Offer } from 'src/app/buisness/offer/models/offer';
import { OfferService } from 'src/app/buisness/offer/services/offer.service';

@Component({
  selector: 'app-create-offer',
  templateUrl: './create-offer.component.html',
  styleUrls: ['./create-offer.component.scss']
})
export class CreateOfferComponent implements OnInit {

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private offerService: OfferService,
  ) { }

  createOfferForm = this.formBuilder.nonNullable.group({
    id: this.getRandomInt().toString(),
    designation: [''],
    description: [''],
    contract: [''],
    salary: [''],
    isApply: false,
    isFavorite: false,
    isBlacklisted: false,
    isOpen: false,
  });

  ngOnInit(): void {
  }

  getRandomInt(): String {
    return Math.floor(Math.random() * 10000000000000000000).toString();
  }

  async onSubmit(): Promise<void> {
    let objet = this.createOfferForm.value as Offer;
    console.log(this.createOfferForm.value);
    this.createOfferForm.reset();
    this.offerService.addOffer(objet);
    this.router.navigate(['/home']);
  }
}
<app-nav></app-nav>
<div class="columns is-mobile mr-0 mt-6">
    <div class="column is-three-fifths is-offset-one-fifth">
        <form [formGroup]="createOfferForm" (ngSubmit)="onSubmit()"> 

            <div class="field">
                <label class="label" for="designation">Designation</label>
                <div class="control">
                  <input class="input" type="text" placeholder="Text input" id="designation" formControlName="designation">
                </div>
              </div>

              <div class="field">
                <label class="label" for="contract">Contract</label>
                <div class="control">
                  <input class="input" type="text" placeholder="Text input" id="contract" formControlName="contract">
                </div>
              </div>

              <div class="field">
                <label class="label" for="dessalaryignation">Salary</label>
                <div class="control">
                  <input class="input" type="text" placeholder="Text input" id="salary" formControlName="salary">
                </div>
              </div>

              <div class="field">
                <label class="label" for="description">Description</label>
                <div class="control">
                  <textarea class="textarea" placeholder="Textarea" id="description" formControlName="description"></textarea>
                </div>
              </div>

              <div class="field is-grouped">
                <div class="control">
                  <button class="button is-primary is-outlined" type="submit">Create</button>
                </div>
              </div>

        </form>
    </div>
</div>
